mapper.select:
/*要执行的sql语句*/
        select id,name,email,age
        from student;

/*要执行的sql语句*/
        select id,name,email,age
        from student;

mapper.insert:
/*insert sql:*/
        insert into student(id, name, email, age)
        values (#{id}, #{name}, #{email}, #{age});

mapper.select:
/*select student by id*/
        select *
        from student
        where id = #{personid};

/*select multi param sql:*/
        select *
        from student
        where name = #{name}
           or age = #{age};

/*select multi object sql:*/
        select *
        from student
        where name = #{queryName}
           or age = #{queryAge};

/*select by name or age sql:*/
        select *
        from student
        where name = #{param1}
           or age = #{param2};

/*select multi map sql:*/
        select *
        from student
        where name = #{name}
           or age = #{age};

/*select return map*/
        select name,email
        from student
        where id = #{id};

/*select student result map sql:*/
        select *
        from student
        where name = #{queryName}
           or age = ${queryAge};

/*select use field alias sql:*/
        select id as stuId,name as stuName,age as stuAge
        from student
        where name = #{queryName}
           or age = #{queryAge};

select *
from student
where name = #{queryName}
   or age = #{queryAge};
